        bytes calldata signature = msgData[msgData.length -
            TxAuthDataVerifierFacetStorage._SIGNATURE_LENGTH:];

        /// Check signature hasn't expired
        if (block.number >= blockExpiration) {
            revert BlockExpired();
        }

        /// Read nonce value and increment it (in storage) to prevent replay attacks
        uint256 userNonce;
        unchecked {
