// SPDX-License-Identifier: MIT
pragma solidity ^0.8.16;

import {Proxy} from "@openzeppelin/contracts/proxy/Proxy.sol";
import "../zkpVerifiers/NexeraVerifierEntrypoint.sol";

/// @title ProxyAavePoolIsEntryPoint
/// @notice This contract acts as a proxy to the Aave Pool on the Mumbai network, integrating with the Nexera verifier for user verification.
contract ProxyAavePoolIsEntryPoint is Proxy, NexeraVerifierEntrypoint {
    // Address of the Aave Pool on the Mumbai network.
    address public constant aavePoolAddress =
        0xcC6114B983E4Ed2737E9BD3961c9924e6216c704;

    /// @dev Returns the address of the Aave Pool implementation contract.
    /// @return The address of the Aave Pool contract.
    function _implementation() internal pure override returns (address) {
        return aavePoolAddress;
    }

    /// @notice Delegates the supply function call to the Aave Pool, requiring user verification.
    /// @param _token Address of the token being supplied.
    /// @param _amount Amount of the token to supply.
    /// @param _user Address of the user on whose behalf the supply is made.
    function supply(
        address _token,
        uint256 _amount,
        address _user
    ) public requiresVerified {
        _delegateWithChecks();
    }

    /// @notice Delegates the supplyWithPermit function call to the Aave Pool, requiring user verification.
    /// @param asset Address of the asset.
    /// @param amount Amount of the asset.
    /// @param onBehalfOf Address of the user on whose behalf the supply is made.
    /// @param referralCode Referral code.
    /// @param deadline Deadline after which the permit is no longer valid.
    /// @param permitV V component of the permit signature.
    /// @param permitR R component of the permit signature.
    /// @param permitS S component of the permit signature.
    function supplyWithPermit(
        address asset,
        uint256 amount,
        address onBehalfOf,
        uint16 referralCode,
        uint256 deadline,
        uint8 permitV,
        bytes32 permitR,
        bytes32 permitS
    ) public requiresVerified {
        _delegateWithChecks();
    }

    /// @dev Fallback function that delegates calls to the Aave Pool, requiring user verification.
    fallback() external payable override requiresVerified {
        _delegateWithChecks();
    }

    /// @dev Internal function to delegate calls to the Aave Pool after checking the implementation address.
    function _delegateWithChecks() internal {
        address _impl = aavePoolAddress;
        require(_impl != address(0), "Implementation address not set");
        _delegate(aavePoolAddress);
    }
}

contracts/interfaces/INexeraVerifierEntr.content:
// SPDX-License-Identifier: GPL-3.0

pragma solidity ^0.8.16;

/// @title INexeraVerifierEntrypoint
/// @notice Interface for the Nexera Verifier Entrypoint, defining essential functions for user verification and scenario verifier management.
interface INexeraVerifierEntrypoint {
    /// @notice Checks if a user is allowed to access the entrypoint.
    /// @param user The address of the user to check.
    /// @return Boolean indicating whether the user is allowed.
    function isAllowedForEntrypoint(address user) external returns (bool);

    /// @notice Adds a new scenario verifier to the system.
    /// @param scenarioVerifierAddress The address of the scenario verifier to be added.
    function addScenarioVerifier(address scenarioVerifierAddress) external;

    /// @notice Deletes a scenario verifier from the system.
    /// @param scenarioVerifierAddress The address of the scenario verifier to be deleted.
    function deleteScenarioVerifier(address scenarioVerifierAddress) external;

    /// @notice Updates a scenario verifier in the system.
    /// @param oldScenarioVerifierAddress The address of the current scenario verifier.
    /// @param newScenarioVerifierAddress The address of the new scenario verifier to replace the old one.
    function updateScenarioVerifier(
        address oldScenarioVerifierAddress,
        address newScenarioVerifierAddress
    ) external;

    /// @notice Enables a scenario verifier for use in the system.
    /// @param scenarioVerifierAddress The address of the scenario verifier to be enabled.
    function enableScenario(address scenarioVerifierAddress) external;

    /// @notice Disables a scenario verifier in the system.
    /// @param scenarioVerifierAddress The address of the scenario verifier to be disabled.
    function disableScenario(address scenarioVerifierAddress) external;
}

contracts/interfaces/IScenarioVerifier.s.content:
// SPDX-License-Identifier: GPL-3.0

pragma solidity ^0.8.16;

import {IZKPVerifier} from "@iden3/contracts/interfaces/IZKPVerifier.sol";

/// @title IScenarioVerifier
/// @notice Interface for the Scenario Verifier, extending the IZKPVerifier interface for scenario-specific verification using Zero-Knowledge Proofs (ZKPs).
interface IScenarioVerifier is IZKPVerifier {
    /// @dev Structure representing a Zero-Knowledge Proof (ZKP) with its associated request ID and proof data.
    /// used in the allowUserForScenario call
    struct ZKP {
        uint64 requestId; // ID of the request associated with this ZKP.
        uint256[] inputs; // Inputs provided for the ZKP.
        uint256[2] a; // Component 'a' of the ZKP.
        uint256[2][2] b; // Component 'b' of the ZKP.
        uint256[2] c; // Component 'c' of the ZKP.
    }

    /// @notice Checks if a user is allowed for the specific scenario.
    /// @param user The address of the user to check.
    /// @return Boolean indicating whether the user is allowed.
    function isAllowedForScenario(address user) external returns (bool);

    /// @notice Processes a batch of ZKPs and attempts to whitelist the user for the scenario.
    /// @param zkps Array of ZKPs to be processed.
    /// @return Boolean indicating the success of the whitelisting process.
    function allowUserForScenario(ZKP[] calldata zkps) external returns (bool);

    /// @notice Finalizes the check process for a user for all rules.
    /// @param user The address of the user to finalize the check for
    /// @return Boolean indicating whether the user was successfully allowed for the scenario.
    function finalizeAllowListScenario(address user) external returns (bool);
}

